<script>
$(document).ready(function() {
    // Function to check if an element's middle point is near the viewport's middle
    function isInViewport(element) {
        var elementTop = $(element).offset().top;
        var elementHeight = $(element).outerHeight();
        var elementMiddle = elementTop + elementHeight / 2;

        var viewportTop = $(window).scrollTop();
        var viewportBottom = viewportTop + $(window).height();
        var viewportMiddle = viewportTop + $(window).height() / 2;

        return elementMiddle >= viewportTop && elementMiddle <= viewportBottom;
    }

    // Function to handle scroll event
    function handleScroll() {
        var activeFound = false;

        for (var i = 1; i <= 5; i++) {
            var targetDiv = '#sticky-status-' + i;
            var targetSticky = '.xe-status-sticky-' + i;

            if (isInViewport($(targetDiv)) && !activeFound) {
                $(targetSticky).addClass('xe-sticky-active');
                activeFound = true;
            } else {
                $(targetSticky).removeClass('xe-sticky-active');
            }
        }
    }

    // Attach the scroll event
    $(window).on('scroll', handleScroll);

    // Initial check in case any elements are already in view on page load
    handleScroll();
});
</script>

<script>
$(document).ready(function() {
    function isHalfInViewport(element) {
        var elementTop = $(element).offset().top;
        var elementHeight = $(element).outerHeight();
        var elementHalf = elementTop + elementHeight / 2;

        var viewportTop = $(window).scrollTop();
        var viewportBottom = viewportTop + $(window).height();

        return elementHalf <= viewportBottom && elementHalf >= viewportTop;
    }

    function isHalfOutViewport(element) {
        var elementTop = $(element).offset().top;
        var elementHeight = $(element).outerHeight();
        var elementHalf = elementTop + elementHeight / 2;

        var viewportTop = $(window).scrollTop();

        return elementHalf < viewportTop;
    }

    function isElementInViewport(element) {
        var elementTop = $(element).offset().top;
        var elementBottom = elementTop + $(element).outerHeight();

        var viewportTop = $(window).scrollTop();
        var viewportBottom = viewportTop + $(window).height();

        return elementBottom > viewportTop && elementTop < viewportBottom;
    }

    function handleScroll() {
        var targetSticky = '.xe-sticky-area';

        // Check if .sticky-area-hidden is in the viewport and .xe-sticky-area-wrapper is out of the viewport
        if (isElementInViewport($('.sticky-area-hidden')) ||
            !isElementInViewport($('.xe-sticky-area-wrapper')) ||
            isElementInViewport($('.xe-sticky-end-swiper'))) {
            $(targetSticky).addClass('xe-translate-x-[-200%]');
            console.log("Removing active class because .sticky-area-hidden is in the viewport and .xe-sticky-area-wrapper is out of the viewport.");
        } 
        else {
          if (isElementInViewport($('.xe-sticky-area-wrapper')) ){
              $(targetSticky).removeClass('xe-translate-x-[-200%]');
            }
            if (isHalfInViewport($('#sticky-status-1'))) {
                $(targetSticky).removeClass('xe-translate-x-[-200%]');
            } 
            
            if (isHalfOutViewport($('#sticky-status-5'))) {
                $(targetSticky).addClass('xe-translate-x-[-200%]');
            }
        }

        if (isElementInViewport($('.sticky-area-hidden')) && 
            !isElementInViewport($('.xe-sticky-area-wrapper'))) {
            $(targetSticky).addClass('xe-translate-x-[-200%]');
            console.log("Removing active class because .sticky-area-hidden is in the viewport and .xe-sticky-area-wrapper is out of the viewport.");
        } 
    }

    // Attach the scroll event
    $(window).on('scroll', handleScroll);

    // Initial check in case any elements are already in view on page load
    handleScroll();
});
</script>
