            $('.item-link').on('click', function() {
                var $list_index = $(this).closest('.list-item').index();
                var $list_index_item = $(this).closest('.list-item');

                $list_index_item.removeClass('active');
                $list_index_item.siblings().removeClass('active');

                if($list_index_item.hasClass('active') === true){

                    $list_index_item.siblings().removeClass('active');
                    $list_index_item.removeClass('active');

                }else{

                    $list_index_item.addClass('active');

                }
            });
        } mediaList();

      $( window ).resize(function() {
          swiper1.destroy(true, true);  // 기존 인스턴스 완전히 제거
          swiper2.destroy(true, true);
            mediaList();
            swiper1.update();
            swiper2.update();
      });


        function stopYoutube () {
            $("iframe").each(function() {
                var src= $(this).attr('src');
                $(this).attr('src',src);
            });
        }


        // 게시물 클릭시 팝업 열기,닫기
        function mediaPopup() {
            var popup_bg = $('.xf-popup-dimmed');
            var popup_cont = $('.xf-popup-content');

            // 게시물 클릭시 팝업 show, swiper슬라이드 플러그인 설정
            $('.item-link').on('click', function() {
                popup_bg.show();
                popup_cont.show().addClass('on');

                var swiper2 = new Swiper('.media-slide-wrap', {
                    speed: 500,
                    slidesPerView: 1,
                    spaceBetween: 0,
                    navigation: {
                        nextEl: '.xf-media-pop-slide-next',
