                        //     }
                        //     return numberAppend(current) + ' / ' + numberAppend(total);
                        // }
                        renderCustom: function (swiper, current, total) {
                            return current + ' / ' + total;
                        }
                    },

                });
                swiper.update();

                function mediaPopCont() {
                    $(document).ready(function(){
                        $('.list').children('.active').each(function(){
                            var $index = $(this).index();
                            swiper.slideTo($index,0,true);

                        });
                    });
                } mediaPopCont();

            });

            // close 버튼 클릭시 팝업 hide
            $('.xf-popup-close').on('click', function() {
                popup_bg.hide();
                popup_cont.hide().removeClass('on');
                stopYoutube();
            });

            $('.xf-play').on('click', function() {
                $(this).hide();
            });

            $('.banner-arrow-wrap').on('click', function() {
                $(this).children().attr({
                    'tabindex':'0',
                    'aria-disabled': false
                });

            });


            // 팝업영역 제외 영역 클릭시 닫힘
            $('.xf-popup-content').on('click', function(e) {
               if($('.xf-popup-content').hasClass('on')){
