<script>
$(document).ready(function() {
    // Function to check if an element's middle point is near the viewport's middle
    function isInViewport(element) {
        var elementTop = $(element).offset().top;
        var elementHeight = $(element).outerHeight();
        var elementMiddle = elementTop + elementHeight / 2;

        var viewportTop = $(window).scrollTop();
        var viewportBottom = viewportTop + $(window).height();
        var viewportMiddle = viewportTop + $(window).height() / 2;

        return elementMiddle >= viewportTop && elementMiddle <= viewportBottom;
    }

    // Function to handle scroll event
    function handleScroll() {
        var activeFound = false;

        for (var i = 1; i <= 5; i++) {
            var targetDiv = '#sticky-status-' + i;
            var targetSticky = '.xe-status-sticky-' + i;

            if (isInViewport($(targetDiv)) && !activeFound) {
                $(targetSticky).addClass('xe-sticky-active');
                activeFound = true;
            } else {
                $(targetSticky).removeClass('xe-sticky-active');
            }
        }
    }

    // Attach the scroll event
    $(window).on('scroll', handleScroll);

    // Initial check in case any elements are already in view on page load
    handleScroll();
});
</script>

